mapper.select:
select o.id as orderId, o.created_time as createdTime, o.pay_method as payMethod, o.real_pay_amount as realPayAmount, o.post_amount as postAmount, os.order_status as orderStatus from orders o left join order_status os on o.id = os.order_id where o.user_id = #{paramsMap.userId} and o.is_delete = 0
and os.order_status = #{paramsMap.orderStatus}
order by o.updated_time DESC

select o.id as orderId, o.created_time as createdTime, o.pay_method as payMethod, o.real_pay_amount as realPayAmount, o.post_amount as postAmount, os.order_status as orderStatus, oi.item_id as itemId, oi.item_name as itemName, oi.item_spec_id as itemSpecId, oi.item_img as itemImg, oi.buy_counts as buyCounts, oi.price as price from orders o left join order_status os on o.id = os.order_id left join order_items oi on o.id = oi.order_id where o.user_id = #{paramsMap.userId} and o.is_delete = 0
and os.order_status = #{paramsMap.orderStatus}
order by o.updated_time DESC
